import { r as resolve, a as relative } from '../shared/nuxi.dae0d833.mjs';
import { c as consola } from '../shared/nuxi.c64eacfe.mjs';
import { w as writeTypes } from '../shared/nuxi.97ffc65c.mjs';
import { l as loadKit } from '../shared/nuxi.1b6f462d.mjs';
import { c as clearDir } from '../shared/nuxi.6a4d1e8c.mjs';
import { o as overrideEnv } from '../shared/nuxi.6840794e.mjs';
import { a as showVersions } from '../shared/nuxi.9ad14ec8.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi.5cb00304.mjs';
import '../shared/nuxi.267db104.mjs';
import 'util';
import 'path';
import 'fs';
import 'os';
import 'tty';
import 'node:fs';
import '../shared/nuxi.e150c956.mjs';
import '../shared/nuxi.77bdf71f.mjs';
import 'node:module';
import 'node:url';
import 'assert';
import '../shared/nuxi.3d7f34cc.mjs';

const buildCommand = defineNuxtCommand({
  meta: {
    name: "build",
    usage: "npx nuxi build [--prerender] [rootDir]",
    description: "Build nuxt for production deployment"
  },
  async invoke(args) {
    overrideEnv("production");
    const rootDir = resolve(args._[0] || ".");
    showVersions(rootDir);
    const { loadNuxt, buildNuxt, useNitro } = await loadKit(rootDir);
    const nuxt = await loadNuxt({
      rootDir,
      overrides: {
        _generate: args.prerender
      }
    });
    const nitro = useNitro?.();
    await clearDir(nuxt.options.buildDir);
    await writeTypes(nuxt);
    nuxt.hook("build:error", (err) => {
      consola.error("Nuxt Build Error:", err);
      process.exit(1);
    });
    await buildNuxt(nuxt);
    if (args.prerender) {
      const dir = nitro?.options.output.publicDir;
      const publicDir = dir ? relative(process.cwd(), dir) : ".output/public";
      consola.success(`You can now deploy \`${publicDir}\` to any static hosting!`);
    }
  }
});

export { buildCommand as default };
