import { buildNuxt } from '@nuxt/kit';
import { r as resolve, a as relative } from '../shared/nuxi.dae0d833.mjs';
import { c as consola } from '../shared/nuxi.c64eacfe.mjs';
import { c as clearDir } from '../shared/nuxi.6a4d1e8c.mjs';
import { l as loadKit } from '../shared/nuxi.1b6f462d.mjs';
import { w as writeTypes } from '../shared/nuxi.97ffc65c.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi.5cb00304.mjs';
import '../shared/nuxi.267db104.mjs';
import 'util';
import 'path';
import 'fs';
import 'os';
import 'tty';
import 'node:fs';
import '../shared/nuxi.77bdf71f.mjs';
import 'node:module';
import 'node:url';
import '../shared/nuxi.e150c956.mjs';

const prepare = defineNuxtCommand({
  meta: {
    name: "prepare",
    usage: "npx nuxi prepare",
    description: "Prepare nuxt for development/build"
  },
  async invoke(args) {
    process.env.NODE_ENV = process.env.NODE_ENV || "production";
    const rootDir = resolve(args._[0] || ".");
    const { loadNuxt } = await loadKit(rootDir);
    const nuxt = await loadNuxt({ rootDir, config: { _prepare: true } });
    await clearDir(nuxt.options.buildDir);
    await buildNuxt(nuxt);
    await writeTypes(nuxt);
    consola.success("Types generated in", relative(process.cwd(), nuxt.options.buildDir));
  }
});

export { prepare as default };
